import { Web3 } from 'web3';

/**
 * Following has demostration of few Web3 Utils methods, for more details visit :
 * https://docs.web3js.org/libdocs/Utils
 */

const web3 = new Web3();

// asciiToHex
console.log(web3.utils.asciiToHex('Hello'));

// checkAddressCheckSum
console.log(
  web3.utils.checkAddressCheckSum('0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed')
);

// encodePacked
console.log(web3.utils.encodePacked('Hello', 'World'));

// fromAscii
console.log(web3.utils.fromAscii('Hello'));

// fromDecimal
console.log(web3.utils.fromDecimal(1234));

// fromTwosComplement
console.log(web3.utils.fromTwosComplement(-1));

// fromUtf8
console.log(web3.utils.fromUtf8('Hello'));

// fromWei
console.log(web3.utils.fromWei('1000000000000000000', 'ether'));

// getStorageSlotNumForLongString
console.log(web3.utils.getStorageSlotNumForLongString('Hello'));

// hexToAscii
console.log(web3.utils.hexToAscii('0x48656c6c6f'));

// hexToBytes
console.log(web3.utils.hexToBytes('0x48656c6c6f'));

// hexToNumber
console.log(web3.utils.hexToNumber('0x4d2'));

// hexToNumberString
console.log(web3.utils.hexToNumberString('0x4d2'));

// hexToString
console.log(web3.utils.hexToString('0x48656c6c6f'));

// hexToUtf8
console.log(web3.utils.hexToUtf8('0x48656c6c6f'));

// isAddress
console.log(web3.utils.isAddress('0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed'));
console.log(web3.utils.isAddress('0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAe'));

// isBloom
console.log(
  web3.utils.isBloom(
    ''
  )
);

// isContractAddressInBloom
console.log(
  web3.utils.isContractAddressInBloom(
    '',
    '0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed'
  )
);

// isHex
console.log(web3.utils.isHex('0x48656c6c6f'));
console.log(web3.utils.isHex('Hello'));

// isHexStrict
console.log(web3.utils.isHexStrict('0x48656c6c6f'));
console.log(web3.utils.isHexStrict('48656c6c6f'));

// isInBloom
console.log(
  web3.utils.isInBloom(
    '',
    '0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed'
  )
);

// isTopic
console.log(web3.utils.isTopic('0x12345678901234567890123456789012'));
console.log(web3.utils.isTopic('0x123456789012345678901234567890123'));

// isTopicInBloom
console.log(
  web3.utils.isTopicInBloom(
    '',
    '0x12345678901234567890123456789012'
  )
);

// isUserEthereumAddressInBloom
console.log(
  web3.utils.isUserEthereumAddressInBloom(
    '',
    '0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed'
  )
);

// keccak256Wrapper
console.log(web3.utils.keccak256Wrapper('Hello'));

// leftPad
console.log(web3.utils.leftPad('0x4d2', 8, '0'));

// numberToHex
console.log(web3.utils.numberToHex(1234));

// padLeft
console.log(web3.utils.padLeft('0x4d2', 8, '0'));

// padRight
console.log(web3.utils.padRight('0x4d2', 8, '0'));

// randomBytes
console.log(web3.utils.randomBytes(32));

// randomHex
console.log(web3.utils.randomHex(32));

// rightPad
console.log(web3.utils.rightPad('0x4d2', 8, '0'));

// sha3
console.log(web3.utils.sha3('Hello'));

// sha3Raw
console.log(web3.utils.sha3Raw('Hello'));

// soliditySha3
console.log(web3.utils.soliditySha3('Hello'));

// stringToHex
console.log(web3.utils.stringToHex('Hello'));

// toAscii
console.log(web3.utils.toAscii('0x48656c6c6f'));

// toBigInt
console.log(web3.utils.toBigInt(1234));
